<?phpclass HomeController extends BaseController {    /*      |--------------------------------------------------------------------------      | Default Home Controller      |--------------------------------------------------------------------------      |      | You may wish to use controllers instead of, or in addition to, Closure      | based routes. That's great! Here is an example controller method to      | get you started. To route to this controller, just add the route:      |      |	Route::get('/', 'HomeController@showWelcome');      |     */    public function index() {        $title = "Home";        return View::make('index')                        ->with('title', $title);    }    public function department($id) {        $page_id = 'dashboard';        $title = 'Dashboard';        $errors = array();        $department = NULL;        if ($id != NULL) {            $deparments = User::departments();            foreach ($deparments as $item) {                if (array_get($item, 'id') == $id) {                    $department = $item;                }            }        }        return View::make('dashboard.department')                        ->with('page_id', $page_id)                        ->with('errors', $errors)                        ->with('title', $title)                        ->with('deparment', $department);    }    public function city($dept, $cit) {        $page_id = 'Dashboard';        $title = 'Dashboard';        $errors = array();        $department = NULL;        $city = NULL;        if ($dept != NULL) {            $deparments = User::departments();            foreach ($deparments as $item) {                if (array_get($item, 'id') == $dept) {                    $department = $item;                    $cities = array_get($department, 'cities');                    foreach ($cities as $c) {                        if (array_get($c, 'id') == $cit) {                            $city = $c;                            break;                        }                    }                }            }        }        return View::make('dashboard.city')                        ->with('page_id', $page_id)                        ->with('errors', $errors)                        ->with('title', $title)                        ->with('department', $department)                        ->with('city', $city);    }}