<?phpclass Admin_BaseController extends Controller {    public $js_files;    public $css_files;    public $page_id;    public $page_title;    public $lang;    public $user;    public $messages = array(        'error' => array(),        'success' => array(),        'info' => array(),        'block' => array() // For Warning messages    );    public function __call($method, $parameters) {        return Response::error('404');    }    public function __construct() {        $this->user = Auth::user();        View::share('user', $this->user);        $this->js_files = array();        $this->css_files = array();        // Retrieving messages from session and removing them        Session::forget('messages');        $m = Session::get('messages');        $m = unserialize((string) $m);        if (!empty($m)) {            foreach ($m as $key => $value)                $this->messages[$key] = array_merge($value, $this->messages[$key]);            Session::forget('messages');        }    }    protected function setupLayout() {        if (!is_null($this->layout)) {            $this->layout = View::make($this->layout);        }    }}